  "Приморское": ["с.Приморка","ст.Морская","п.Новоприморский"],
  "Самбекское": ["с.Самбек","х.Курлацкий","х.Некрасовка"],
  "Синявское": ["с.Синявское","х.Мержаново","х.Морской Чулек","х.Халыбо-Адабашев","х.Водино"],
  "Советинское": ["слоб.Советска","с.Горская Порада","с.Новостроенка","х.Любовка","х.Садки","х.Приют"],
  "Троицкое": ["с.Троицкое","п.Федосеевка","с.Кошкино"],
