// Обратный словарь село->поселение
const seloToPoselenie = {};
Object.entries(poseleniaMap).forEach(([poselenie, sela]) => {
  sela.forEach(selo => {
    seloToPoselenie[selo.trim()] = poselenie;
  });
});

// Добавляем поле poselenie к ветеранам, фильтруем только известные села
const filteredVets = veterans
  .map(vet => {
    const seloKey = vet.short.trim();
    const poselenie = seloToPoselenie[seloKey];
    if (!poselenie) return null;
    return {...vet, poselenie};
  })
  .filter(Boolean);

const poseleniaList = Object.keys(poseleniaMap).sort();

const select = document.getElementById('poselenie-select');
const container = document.getElementById('veterans-container');

// Заполнение селектора поселений
poseleniaList.forEach(pos => {
  const option = document.createElement('option');
  option.value = pos;
  option.textContent = pos;
  select.appendChild(option);
});

// По-умолчанию выбираем первое поселение и показываем
if (poseleniaList.length > 0) {
  select.value = poseleniaList[0];
  render(poseleniaList[0]);
}

function render(poselenieFilter) {
  container.innerHTML = '';

  const vetsToShow = filteredVets.filter(v => v.poselenie === poselenieFilter);

  const posDiv = document.createElement('div');
  posDiv.className = 'poselenie-block';

  const h2 = document.createElement('h2');
  h2.className = 'poselenie-title';
  h2.textContent = poselenieFilter;
  posDiv.appendChild(h2);

  const divList = document.createElement('div');
  divList.className = 'vet-list';

  vetsToShow.forEach((vet, i) => {
    const card = document.createElement('div');
    card.className = 'vet-card';
    card.tabIndex = 0;
    card.setAttribute('aria-label', `Подробнее о ветеране ${vet.name}`);
    card.setAttribute('data-modal', `modal-${poselenieFilter}-${i}`);
    card.innerHTML = `
      <img src="${vet.img}" alt="${vet.name}" />
      <div class="vet-name">${vet.name}</div>
      <div class="vet-short">${vet.short}</div>
    `;
    divList.appendChild(card);

    if (!document.getElementById(`modal-${poselenieFilter}-${i}`)) {
      const modal = document.createElement('div');
      modal.className = 'modal';
      modal.id = `modal-${poselenieFilter}-${i}`;
      modal.setAttribute('role', 'dialog');
      modal.setAttribute('aria-modal', 'true');
      modal.setAttribute('aria-labelledby', `modal-${poselenieFilter}-${i}-title`);
      modal.innerHTML = `
        <div class="modal-content">
          <button class="modal-close" aria-label="Закрыть">&times;</button>
          <h2 id="modal-${poselenieFilter}-${i}-title" tabindex="0">${vet.name}</h2>
          <img src="${vet.img}" alt="${vet.name}" />
          <p>${vet.info || 'Информация отсутствует.'}</p>
          ${vet.source ? `<a href="${vet.source}" target="_blank" rel="noopener noreferrer" class="source-link" tabindex="0">Источник</a>` : ''}
        </div>
      `;
      document.body.appendChild(modal);
    }
  });

  posDiv.appendChild(divList);
  container.appendChild(posDiv);

  activateModalHandlers();
}

function activateModalHandlers() {
  document.querySelectorAll('.vet-card').forEach(card => {
    card.onclick = () => openModal(card.getAttribute('data-modal'));
    card.onkeydown = e => {
      if (e.key === "Enter" || e.key === " ") {
        e.preventDefault();
        openModal(card.getAttribute('data-modal'));
      }
    };
  });

  document.querySelectorAll('.modal-close').forEach(btn => {
    btn.onclick = () => closeModal(btn.closest('.modal'));
  });

  document.querySelectorAll('.modal').forEach(modal => {
    modal.onclick = e => {
      if (e.target === modal) closeModal(modal);
    };
  });

  document.onkeydown = e => {
    if (e.key === 'Escape') {
      document.querySelectorAll('.modal').forEach(modal => {
        if (modal.style.display === 'block') closeModal(modal);
      });
    }
  };
}

function openModal(id) {
  const modal = document.getElementById(id);
  if (modal) {
    modal.style.display = 'block';
    document.body.style.overflow = 'hidden';
    modal.animate([{opacity: 0, transform: 'scale(0.9)'}, {opacity: 1, transform: 'scale(1)'}], {duration: 300, fill: 'forwards'});
    const h2 = modal.querySelector('h2');
    if(h2) h2.focus();
  }
}

function closeModal(modal) {
  modal.animate([{opacity: 1, transform: 'scale(1)'}, {opacity: 0, transform: 'scale(0.9)'}], {duration: 300, fill: 'forwards'}).onfinish = () => {
    modal.style.display = 'none';
    document.body.style.overflow = '';
  };
}

select.addEventListener('change', e => {
  render(e.target.value);
});
